# Ассоцииции по первичному ключу (Primary key assotiations)

![one-to-one](/blog/articles/hibernate/relations/one-to-one.png "One-to-one")

Таблица "person":

	create table hibernate.person (
		id integer not null auto_increment,
		name varchar(100) not null,
		primary key(id)
	);

Таблица "passport":

	create table hibernate.passport (
		id integer not null,
		nationality varchar(45) not null,
		date_of_issue date not null
	);

[![one-to-one-uml](/blog/assets_c/2009/07/one-to-one-uml-thumb-520x179-74.png "One-to-one UML")](/blog/articles/hibernate/relations/one-to-one-uml.png)

Файл "Person.java":

	package de.inger;

	import java.util.Date;

	public class Passport {

		private Integer id;
		private String nationality;
		private Date dateOfIssue;
		private Person person;

		public Passport() {
		}

		public Passport(String nationality, Date dateOfIssue, Person person) {
			setNationality(nationality);
			setDateOfIssue(dateOfIssue);
			setPerson(person);
		}

		public void setId(Integer id) {
			this.id = id;
		}

		public Integer getId() {
			return id;
		}

		public void setNationality(String nationality) {
			this.nationality = nationality;
		}

		public String getNationality() {
			return nationality;
		}

		public void setDateOfIssue(Date dateOfIssue) {
			this.dateOfIssue = dateOfIssue;
		}

		public Date getDateOfIssue() {
			return dateOfIssue;
		}

		public void setPerson(Person person) {
			this.person = person;
		}

		public Person getPerson() {
			return person;
		}
	}

Файл "Passport.java":

	package de.inger;

	import java.util.Date;

	public class Passport {

		private Integer id;
		private String nationality;
		private Date dateOfIssue;
		private Person person;

		public Passport() {
		}

		public Passport(String nationality, Date dateOfIssue, Person person) {
			setNationality(nationality);
			setDateOfIssue(dateOfIssue);
			setPerson(person);
		}

		public void setId(Integer id) {
			this.id = id;
		}

		public Integer getId() {
			return id;
		}

		public void setNationality(String nationality) {
			this.nationality = nationality;
		}

		public String getNationality() {
			return nationality;
		}

		public void setDateOfIssue(Date dateOfIssue) {
			this.dateOfIssue = dateOfIssue;
		}

		public Date getDateOfIssue() {
			return dateOfIssue;
		}

		public void setPerson(Person person) {
			this.person = person;
		}

		public Person getPerson() {
			return person;
		}
	}


Файл "mapping.hbm.xml":

	<?xml version="1.0"?>
	<!DOCTYPE hibernate-mapping PUBLIC 
	"-//Hibernate/Hibernate Mapping DTD 3.0//EN"
	"http://hibernate.sourceforge.net/hibernate-mapping-3.0.dtd">	
	<hibernate-mapping>
		<class name="de.inger.dao.Person" table="person">
			<id name="id" type="integer">
				<column name="id" />
				<generator class="native" />
			</id>
			<property name="name" type="string">
				<column name="name" />
			</property>
			<one-to-one name="passport" cascade="all" />
		</class>	
		<class name="de.inger.dao.Passport" table="passport">
			<id name="id" type="integer">
				<column name="id" />
				<generator class="foreign">
					<param name="property">person</param>
				</generator>
			</id>
			<property name="nationality" type="string">
				<column name="nationality" />
			</property>
			<property name="dateOfIssue" type="date">
				<column name="date_of_issue" />
			</property>
			<one-to-one name="person" constrained="true" />
		</class>
	</hibernate-mapping>

## Ассоцииации по уникальному внешнему ключу (Unique foreign key associations)

![one-to-one-particular-case](/blog/articles/hibernate/relations/one-to-many-particular-case.png "One to one particular case")

Таблица "post":

	create table hibernate.post (
		id integer not null auto_increment,
		content longtext not null,
		primary key(id)
	);

Таблица "metadata":

	create table hibernate.metadata (
		id integer not null auto_increment,
		content longtext not null,
		post_id integer,
		primary key(id),
		unique key(post_id)
	);

[![one-to-one-particular-case-uml](/blog/assets_c/2009/08/one-to-many-particular-case-uml-thumb-520x213-89.png "One-to-one")](/blog/articles/hibernate/relations/one-to-many-particular-case-uml.png)

Файл "Post.java"

	package de.inger;

	import java.util.Set;

	public class Post {
		private Integer id;
		private String content;
		private Set<Metadata> metadata;

		public Post() {
		}

		public Post(String content) {
			setContent(content);
		}

		public void setId(Integer id) {
			this.id = id;
		}

		public Integer getId() {
			return id;
		}

		public void setContent(String content) {
			this.content = content;
		}

		public String getContent() {
			return content;
		}

		public void setMetadata(Set<Metadata> metadata) {
			this.metadata = metadata;
		}

		public Set<Metadata> getMetadata() {
			return metadata;
		}
	}

Файл "Metadata.java"

	package de.inger;

	public class Metadata {

		private Integer id;
		private String content;
		private Post post;

		public Metadata() {
		}

		public Metadata(String content) {
			setContent(content);
		}

		public void setId(Integer id) {
			this.id = id;
		}

		public Integer getId() {
			return id;
		}

		public void setContent(String content) {
			this.content = content;
		}

		public String getContent() {
			return content;
		}

		public void setPost(Post post) {
			this.post = post;
		}

		public Post getPost() {
			return post;
		}		
	}

Файл "mapping.hbm.xml":

	<?xml version="1.0"?>
	<!DOCTYPE hibernate-mapping PUBLIC 
	"-//Hibernate/Hibernate Mapping DTD 3.0//EN"
	"http://hibernate.sourceforge.net/hibernate-mapping-3.0.dtd">
	<hibernate-mapping>
		<class name="de.inger.Post" table="post">
			<id name="id" type="integer">
				<column name="id" />
				<generator class="native" />
			</id>
			<property name="content" type="text">
				<column name="content" />
			</property>
			<set name="metadata" cascade="all">
				<key column="post_id" />
				<one-to-many class="de.inger.Metadata" />
			</set>
		</class>
		<class name="de.inger.Metadata" table="metadata">
			<id name="id" type="integer">
				<column name="id" />
				<generator class="native" />
			</id>
			<property name="content" type="text">
				<column name="content" />
			</property>
			<many-to-one name="post" unique="true">
				<column name="post_id" />
			</many-to-one>
		</class>
	</hibernate-mapping>

![one-to-many](/blog/articles/hibernate/relations/one-to-many.png "One to many")

Таблица "posts":

    create table posts (
        id integer not null auto_increment,
        content longtext not null,
        primary key (id)
    );

Таблица "comments":

    create table comments (
        id integer not null auto_increment,
        content longtext not null,
        post_id integer not null,
        primary key (id)
    );

![one-to-many-uml](/blog/assets_c/2009/08/one-to-many-uml-thumb-520x223-92.png "One to many")

Файл "Post.java":

    package de.inger;

    import java.util.Set;

    public class Post {

        private Integer id;   
        private String content;
        private Set<Comment> comments;

        public void setContent(String content) {
            this.content = content;
        }

        public String getContent() {
            return content;
        }

        public void setComments(Set<Comment> comments) {
            this.comments = comments;
        }

        public Set<Comment> getComments() {
            return comments;
        }

        public void setId(Integer id) {
            this.id = id;
        }

        public Integer getId() {
            return id;
        }
    }

Файл "Comment.java":

	package de.inger;

	public class Comment {

		private Integer id;	
		private String content;
		private Post post;

		public void setContent(String content) {
			this.content = content;
		}

		public String getContent() {
			return content;
		}

		public void setPost(Post post) {
			this.post = post;
		}

		public Post getPost() {
			return post;
		}

		public void setId(Integer id) {
			this.id = id;
		}

		public Integer getId() {
			return id;
		}	
	}

Файл "mapping.hbm.xml":

	<?xml version="1.0"?>
	<!DOCTYPE hibernate-mapping PUBLIC 
	"-//Hibernate/Hibernate Mapping DTD 3.0//EN"
	"http://hibernate.sourceforge.net/hibernate-mapping-3.0.dtd">

	<hibernate-mapping>
		<class name="de.inger.Post" table="posts">
			<id name="id" type="integer" column="id">
				<generator class="native" />
			</id>
			<property name="content" type="text" column="content" />
			<set name="comments" cascade="all">
				<key column="post_id" />
				<one-to-many class="de.inger.Comment" />
			</set>
		</class>
		<class name="de.inger.Comment" table="comments">
			<id name="id" type="integer" column="id">
				<generator class="native" />
			</id>
			<property name="content" type="text" column="content" />
			<many-to-one name="post" column="post_id" cascade="all" />
		</class>
	</hibernate-mapping>

![one-to-many-parent-children](/blog/articles/hibernate/relations/one-to-many-parent-children.png "One to many (parent-children)")

Таблица "comments":

	create table comments (
		id integer not null auto_increment,
		content longtext not null,
		parent_id integer,
		primary key (id)
	);

![one-to-many-parent-children-uml](/blog/assets_c/2009/08/one-to-many-parent-children-uml-thumb-520x225-98.png "one-to-many-parent-children-uml")


Файл "Comment.java":

	package de.inger;

	import java.util.Set;

	public class Comment {

		private Integer id;	
		private String content;
		private Comment parent;
		private Set<Comment> children;

		public Comment() {		
		}

		public Comment(String content) {
			setContent(content);
		}

		public void setContent(String content) {
			this.content = content;
		}

		public String getContent() {
			return content;
		}

		public void setId(Integer id) {
			this.id = id;
		}

		public Integer getId() {
			return id;
		}

		public void setParent(Comment parent) {
			this.parent = parent;
		}

		public Comment getParent() {
			return parent;
		}

		public void setChildren(Set<Comment> children) {
			this.children = children;
		}

		public Set<Comment> getChildren() {
			return children;
		}	
	}


Файл "mapping.hbm.xml":

	<?xml version="1.0"?>
	<!DOCTYPE hibernate-mapping PUBLIC 
	"-//Hibernate/Hibernate Mapping DTD 3.0//EN"
	"http://hibernate.sourceforge.net/hibernate-mapping-3.0.dtd">	
	<hibernate-mapping>
		<class name="de.inger.Comment" table="comments">
			<id name="id" type="integer" column="id">
				<generator class="native" />
			</id>
			<set name="children" cascade="all">
				<key column="parent_id" />
				<one-to-many class="de.inger.Comment" />
			</set>
			<property name="content" type="text" column="content" />
			<many-to-one name="parent" column="parent_id" cascade="all" />
		</class>
	</hibernate-mapping>

![many-to-many](/blog/articles/hibernate/relations/many-to-many.png "Many to many") 

Таблица "posts":

	create table posts (
		id integer not null auto_increment,
		content longtext not null,
		primary key (id)
	);

Таблица "categories":

	create table categories (
		id integer not null auto_increment,
		name longtext not null,
		primary key (id)
	);

Таблица "post_category":

	create table post_category (
		post_id integer not null,
		category_id integer not null,	
		primary key (post_id, category_id)
	);


[![many-to-many-uml](/blog/assets_c/2009/08/many-to-many-uml-thumb-520x227-100.png "many-to-many-uml")](/blog/articles/hibernate/relations/many-to-many-uml.png)

Файл "Post.java":

	package de.inger;

	import java.util.Set;

	public class Post {

		private Integer id;
		private String content;
		private Set<Category> categories;

		public void setId(Integer id) {
			this.id = id;
		}
		public Integer getId() {
			return id;
		}
		public void setContent(String content) {
			this.content = content;
		}
		public String getContent() {
			return content;
		}
		public void setCategories(Set<Category> categories) {
			this.categories = categories;
		}
		public Set<Category> getCategories() {
			return categories;
		}
	}

Файл "Category.java":

	package de.inger;

	import java.util.Set;

	public class Category {

		private Integer id;
		private String name;
		private Set<Post> posts;

		public void setId(Integer id) {
			this.id = id;
		}
		public Integer getId() {
			return id;
		}
		public void setName(String name) {
			this.name = name;
		}
		public String getName() {
			return name;
		}
		public void setPosts(Set<Post> posts) {
			this.posts = posts;
		}
		public Set<Post> getPosts() {
			return posts;
		}

	}


Файл "mapping.hbm.xml":

	<?xml version="1.0"?>
	<!DOCTYPE hibernate-mapping PUBLIC 
	"-//Hibernate/Hibernate Mapping DTD 3.0//EN"
	"http://hibernate.sourceforge.net/hibernate-mapping-3.0.dtd">
	<hibernate-mapping>
		<class name="de.inger.Post" table="posts">
			<id name="id" type="integer" column="id">
				<generator class="native" />
			</id>
			<property name="content" type="text" column="content" />
			<set name="categories" table="post_category" cascade="all">
				<key column="post_id" />
				<many-to-many class="de.inger.Category" column="category_id" />
			</set>
		</class>
		<class name="de.inger.Category" table="categories">
			<id name="id" type="integer" column="id">
				<generator class="native" />
			</id>
			<property name="name" type="text" column="name" />
			<set name="posts" table="post_category" cascade="all">
				<key column="category_id" />
				<many-to-many class="de.inger.Post" column="post_id" />
			</set>
		</class>
	</hibernate-mapping>
